CREATE TABLE IF NOT EXISTS `department` (`id` int(11) NOT NULL AUTO_INCREMENT,PRIMARY KEY (`id`),`name` varchar(255) NULL DEFAULT 'test',`code` varchar(255) NULL DEFAULT NULL) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `employee` (`id` int(11) NOT NULL AUTO_INCREMENT,PRIMARY KEY (`id`),`firstname` varchar(255) NULL DEFAULT NULL,`surname` varchar(255) NULL DEFAULT NULL,`department_id` int(11) NULL DEFAULT NULL,KEY `department_id` (`department_id`),KEY `name` (`firstname`,`surname`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `employee_collegues` (`employee_id` int(11) NOT NULL,`collegue_id` int(11) NOT NULL, PRIMARY KEY (`employee_id`,`collegue_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `client` (`id` int(11) NOT NULL AUTO_INCREMENT,PRIMARY KEY (`id`),`name` varchar(255) NULL DEFAULT NULL,UNIQUE KEY `test` (`name`),KEY `test2` (`name`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `client_contacts` (`client_id` int(11) NOT NULL,`contact_id` int(11) NOT NULL, PRIMARY KEY (`client_id`,`contact_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `instance` (`id` int(11) NOT NULL AUTO_INCREMENT,PRIMARY KEY (`id`),`hash` varchar(255) NULL DEFAULT NULL, UNIQUE(`hash`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `database` (`id` int(11) NOT NULL AUTO_INCREMENT,PRIMARY KEY (`id`),`url` varchar(255) NULL DEFAULT NULL,`instance_id` int(11) NULL DEFAULT NULL,KEY `instance_id` (`instance_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `endpoint` (`id` int(11) NOT NULL AUTO_INCREMENT,PRIMARY KEY (`id`),`url` varchar(255) NULL DEFAULT NULL,`database_id` int(11) NULL DEFAULT NULL,KEY `database_id` (`database_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `schema` (`id` int(11) NOT NULL AUTO_INCREMENT,PRIMARY KEY (`id`),`configJSON` longtext NULL DEFAULT NULL,`version` varchar(255) NULL DEFAULT NULL) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `schema_databases` (`schema_id` int(11) NOT NULL,`database_id` int(11) NOT NULL, PRIMARY KEY (`schema_id`,`database_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;